mapper.update:
update commodity
set
  trade_name = #{tradeName,jdbcType=VARCHAR},
  points = #{points,jdbcType=INTEGER},
  introduce = #{introduce,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from commodity
where deletes = 0 and id = #{id,jdbcType=INTEGER}

mapper.update:
update commodity
set
  picture = #{picture,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into commodity (trade_name, points,
  introduce
  )
values (#{tradeName,jdbcType=VARCHAR}, #{points,jdbcType=INTEGER},
  #{introduce,jdbcType=VARCHAR}
  )

