mapper.select:
select

from user_inf,user_role
where   user_inf.telephone = user_role.telephone and
( username LIKE #{userName,jdbcType=VARCHAR}
or user_inf.telephone LIKE #{telephone,jdbcType=VARCHAR}
)

mapper.update:
update user_inf
set
  telephone = #{NewTelephone,jdbcType=VARCHAR}
where telephone = #{telephone,jdbcType=VARCHAR}

update user_inf
set
  email = #{NewEmail,jdbcType=VARCHAR}
where telephone = #{telephone,jdbcType=VARCHAR}

update user_inf
set
  email = #{NewEmail,jdbcType=VARCHAR}
where telephone = #{telephone,jdbcType=VARCHAR}

update user_inf
set
  password = #{NewPassword,jdbcType=VARCHAR}
where telephone = #{telephone,jdbcType=VARCHAR}

update user_inf
set
  password = #{NewPassword,jdbcType=VARCHAR}
where telephone = #{telephone,jdbcType=VARCHAR}

update user_inf
set
  picture = #{uploadFile,jdbcType=VARCHAR}
where telephone = #{telephone,jdbcType=VARCHAR}

update user_inf
set
  integral = integral + #{sorce,jdbcType=INTEGER}
  where telephone = #{telephone,jdbcType=VARCHAR}
